mapper.update:
update klass
set klass_time = #{klassTime}
where id = #{klassId}

update klass
set klass_location = #{klassPlace}
where id = #{klassId}

mapper.select:
select klass_id
from klass_student
where course_id = #{courseId}
  and student_id = #{studentId}

mapper.delete:
delete
from klass_round
where klass_id = #{klassId}
  and round_id = #{roundId}
