mapper.update:
update klass
set klass_time = #{klassTime}
where id = #{klassId}

update klass
set klass_location = #{klassPlace}
where id = #{klassId}

mapper.delete:
delete
from klass_round
where klass_id = #{klassId}
  and round_id = #{roundId}

mapper.update:
update team_student
set student_id = #{studentId}
where team_id = #{teamId}

mapper.delete:
delete
from team_student
where team_id = #{teamId}
and student_id = #{student.studentId}
