mapper.select:
SELECT seminar_id as seminarId, course_id as courseId, topic, introduction, visible
FROM seminar_info
WHERE course_id = #{courseId}

SELECT seminar_id as seminarId, course_id as courseId, topic, introduction, visible
FROM seminar_info
WHERE course_id = #{seminarId}

mapper.update:
UPDATE seminar_info
SET course_id    = #{courseId},
    topic        = #{topic},
    introduction = #{introduction},
    visible      = #{visible}
WHERE seminar_id = #{seminarId}
