mapper.update:
UPDATE seminar
SET round_id        = #{roundId},
    max_team_num    = #{maxTeamNum},
    sign_start_time = #{signStartTime},
    sign_end_time   = #{signEndTime}
WHERE seminar_id = #{seminarId}
  AND turning_class_id = #{turningClassId}

UPDATE seminar
SET status = 1
WHERE seminar_id = #{seminarId}
  AND turning_class_id = #{turningClassId}

UPDATE seminar
SET status = 2
WHERE seminar_id = #{seminarId}
  AND turning_class_id = #{turningClassId}

UPDATE seminar
SET status = 3
WHERE seminar_id = #{seminarId}
  AND turning_class_id = #{turningClassId}
