mapper.update:
UPDATE seminar
SET round_id          = #{roundId},
    max_team          = #{maxTeamNum},
    enroll_start_date = #{signStartTime},
    enroll_end_date   = #{signEndTime}
WHERE id = #{seminarId}

UPDATE clbum_seminar
SET state = 1
WHERE seminar_id = #{seminarId}
  AND clbum_id = #{clbumId}

UPDATE clbum_seminar
SET state = 2
WHERE seminar_id = #{seminarId}
  AND clbum_id = #{clbumId}

UPDATE clbum_seminar
SET state = 3
WHERE seminar_id = #{seminarId}
  AND clbum_id = #{clbumId}
